INSERT INTO `text` VALUES ('2208', '铃木珠西', '2967499861', '2022-03-13 09:13:16', '广东 珠海', 'IP属地：广东', '回复<a href=\"/n/NaranjaAdorable\">@NaranjaAdorable</a>:我觉得生命权还是要优先保障，每天死亡的病例里都愿意为了自由而接受死亡的后果吗？不一定吧……且不说发展成普通流感甚至消失需要多长时间和概率，我相信国家都会有相应的药物和政策在研制(爆发初期决定五种疫苗同时研发的合理猜测)');
