INSERT INTO `text` VALUES ('2656', '东海边的穿条鱼不是阿平', '1849255227', '2022-03-10 21:54:23', '江苏 苏州', 'IP属地：江苏', '不排除一部分人事不了解病毒，但是看不到学校的后续措施，在没有药、缺乏生活用品的情况下肯定会恐慌啊……我留学的同学，阳了也会自己隔离吃药，他们啥也没有，也没有隔离条件，肯定着急<span class=\"url-icon\"><img alt=[生病] src=\"https://h5.sinaimg.cn/m/emoticon/icon/default/d_shengbing-b740fa9852.png\" style=\"width:1em; height:1em;\" /></span>');
