INSERT INTO `text` VALUES ('2961', '-星视觉-', '7391604889', '2022-03-11 01:44:31', '其他', 'IP属地：上海', '前几天我们大楼打扫的阿姨还在说，以后新冠就跟感冒一样，得病了觉得严重就自己去医院。观念倒比我先进<span class=\"url-icon\"><img alt=[允悲] src=\"https://h5.sinaimg.cn/m/emoticon/icon/default/d_yunbei-a14a649db8.png\" style=\"width:1em; height:1em;\" /></span> 看看HK这波致病率怎么样吧，会不会让医疗资源超负荷。');
INSERT INTO `text` VALUES ('2962', 'whitetowhite', '6195106945', '2022-03-10 22:21:20', '其他', 'IP属地：江西', '根本不在于病毒致不致死，而在于处理方法，心寒的根本，是看透了在危机事件面前，学校和政府拉胯的应对能力，这次是病毒不致死，下次呢？如果是有关生死的呢？学生们市民们怎么敢把命交到他们手上啊？！');
