INSERT INTO `text` VALUES ('3001', '说清楚了再问不答', '1009776534', '2022-03-10 23:49:36', '其他', 'IP属地：湖北', '回复<a href=\"/n/小水不在家o\">@小水不在家o</a>:一个幼儿园娃哭了：老师，小明打我。我会共情。一个小学生哭了：老师，小明抢了我的笔。我会引导他思考谁对谁错。一个中学生哭了：老师，小明欺负我。我会说请你的家长跟小明的家长协商解决。一个大学生说：老师，小明欺负我，说实话，为了尊重，我会考虑一下该怎么回应。区别。');
