INSERT INTO `text` VALUES ('3378', '-星视觉-', '7391604889', '2022-03-11 02:37:23', '其他', 'IP属地：上海', '回复<a href=\"/n/阿司匹林42195米\">@阿司匹林42195米</a>:我家楼下的大白都不紧张 封条拉好后，还帮我把外卖递进来了。然后大家在说，在家隔离比集中隔离要好，先关48小时等检测吧。上海这几天东一栋西一栋封，我看市民朋友情绪都挺稳定的，基本总能听到朋友同事什么家里楼封了<span class=\"url-icon\"><img alt=[允悲] src=\"https://h5.sinaimg.cn/m/emoticon/icon/default/d_yunbei-a14a649db8.png\" style=\"width:1em; height:1em;\" /></span>');
