INSERT INTO `text` VALUES ('3380', '知寒碎碎念', '3873656748', '2022-03-10 23:44:39', '吉林', 'IP属地：吉林', '不知道事情原委的人别来评论什么现象了。学生恐慌的根本不是新冠，是没有人手没有物资没有隔离条件，阳性阴性混住，没有饭没有水阳台上上厕所女生没有姨妈巾没有消毒用品，志愿者的防护服都不够，而这种情况持续了三天，过度的压热度以学位证威胁学生删微博，这才导致了大家的害怕和发声。');
