INSERT INTO `text` VALUES ('3385', '沙漠红柳1', '5797880824', '2022-03-11 12:20:45', '其他', 'IP属地：广东', '抗疫2年多，卫健委依然没有提供可随时查询的诚实的透明的疫情信息，比如多少无症状，多少轻症重症，这些人如何治疗，后果怎样，有无后遗症，打疫苗情况等等基本信息，所以连大学生这个获取信息能力最强的群体都对这个持续2年多热点的传染病连基本的常识都没有，谁之过？');
