INSERT INTO `text` VALUES ('3582', 'Kaeris', '5667190718', '2022-03-10 23:32:40', '辽宁', 'IP属地：福建', '回复<a href=\"/n/米蜜秋\">@米蜜秋</a>:六个小时换一片，一天换至少三四次，七天就是三包四五六十，基本就是打包卖的一半量，还要怎么屯？没法共情站着说话不腰疼');
INSERT INTO `text` VALUES ('3583', '甜的小小仙人球', '6630768042', '2022-03-10 23:16:39', '其他', 'IP属地：辽宁', '回复<a href=\"/n/米蜜秋\">@米蜜秋</a>:谢谢您，可真贴心，告诉您个冷知识，卫生巾也有保质期，存放太久容易受潮受污染');
