INSERT INTO `text` VALUES ('3603', 'WLYYYYYYYY', '1791415917', '2022-03-11 00:06:39', '宁夏 银川', 'IP属地：北京', '疫情本身并不令人恐惧的，令人恐惧的是万一感染后无死角的公开流调、承受被舆论压垮的风险、甚至工作生活节奏完全被打乱的压力，这两年的生活看似没什么改变但又似乎被改变了很多，正确认识疫情带来的影响和不过分传播恐慌才是zf该做的事情吧。');
